function burgerMenu(){const e=document.querySelector(".burger"),c=document.querySelector(".menu"),t=document.querySelector("body");e.addEventListener("click",(()=>{c.classList.contains("active")?(c.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked")):(c.classList.add("active"),e.classList.add("active-burger"),t.classList.add("locked"))})),window.addEventListener("resize",(()=>{window.innerWidth>767.98&&(c.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked"))}))}function accordion(){document.querySelectorAll(".accordion__item-trigger").forEach((e=>{e.addEventListener("click",(()=>{const c=e.parentNode;c.classList.contains("accordion__item-active")?c.classList.remove("accordion__item-active"):(document.querySelectorAll(".accordion__item").forEach((e=>e.classList.remove("accordion__item-active"))),c.classList.add("accordion__item-active"))}))}))}function togglePricing(){const e=document.querySelector(".switcher"),c=document.querySelectorAll(".bulling-item");e.addEventListener("click",(()=>{e.classList.toggle("active"),e.classList.contains("active")?(c[0].classList.remove("active"),c[2].classList.add("active")):(c[0].classList.add("active"),c[2].classList.remove("active"))}))}burgerMenu(),accordion(),togglePricing();